actionConfiguration.body:
() => {
  let a = Issues_SP_resolved.data[0]['points_resolved'];
  let b = Previous_sprint_OBJ.myFun1().resolved;
  let message;
  let color;
  let percent = (Math.abs(a - b) * 100 / a).toFixed(2);
  console.log(a, b);
  if (a < b) {
    message = percent.toString() + "% more on the last sprint";
    color = '#16a34a';
    return {
      message: message,
      color: color
    };
  }
  if (a > b) {
    message = percent.toString() + "% less on the last sprint";
    color = '#dc2626';
    return {
      message: message,
      color: color
    };
  }
  if (a = b) {
    message = "Equal to the last sprint";
    color = '#a1a1aa';
    return {
      message: message,
      color: color
    };
  }
}
